function drawBall(){ctx.beginPath(),ctx.arc(x,y,ballRadius,0,2*Math.PI),ctx.fillStyle="#0095DD",ctx.fill(),ctx.closePath()}function draw(){ctx.clearRect(0,0,canvas.width,canvas.height),drawBall(),x+=dx,y+=dy,(y+dy>canvas.height-ballRadius||y+dy<ballRadius)&&(dy=-dy),(x+dx>canvas.width-ballRadius||x+dx<ballRadius)&&(dx=-dx)}var canvas=document.getElementById("myCanvas"),ctx=canvas.getContext("2d"),x=canvas.width/2,y=canvas.height-30,dx=2,dy=-2,ballRadius=10;setInterval(draw,10);